import requests
import json
from datetime import datetime

def create_photography_todo_list():
    """Create a comprehensive todo list for photography project"""

    url = "http://localhost:8000/chat/chat"

    request_data = {
        "user_id": "photographer_001",
        "conversation_id": "conv_photography_planning_2025",
        "prompt": "I need help setting up a comprehensive todo list for my photography project. I'm working on a portfolio project that includes portrait photography, landscape shots, and event photography. I need to organize my tasks, plan my shoots, and manage my post-processing workflow. Can you help me create a structured todo list with deadlines and priorities?",
        "projects": [
            {
                "project_id": "proj_portfolio_2025",
                "title": "Photography Portfolio 2025",
                "description": "Comprehensive portfolio showcasing portrait, landscape, and event photography skills",
                "instructor": "Self-directed"
            }
        ],
        "tasks": [
            {
                "task_id": "task_portrait_shoot_1",
                "title": "Portrait Photography Session - Studio Setup",
                "description": "Conduct portrait photography session with professional lighting setup",
                "due_datetime": "2025-01-15T14:00:00.000Z",
                "status": "not_started",
                "project_id": "proj_portfolio_2025"
            },
            {
                "task_id": "task_landscape_scouting",
                "title": "Landscape Location Scouting",
                "description": "Research and visit potential landscape photography locations",
                "due_datetime": "2025-01-20T09:00:00.000Z",
                "status": "not_started",
                "project_id": "proj_portfolio_2025"
            },
            {
                "task_id": "task_equipment_check",
                "title": "Equipment Maintenance and Check",
                "description": "Clean lenses, check camera settings, and ensure all equipment is ready",
                "due_datetime": "2025-01-10T10:00:00.000Z",
                "status": "not_started",
                "project_id": "proj_portfolio_2025"
            }
        ],
        "stage": "thinking",
        "metadata": {
            "project_type": "photography",
            "skill_level": "intermediate",
            "timeline": "3_months",
            "equipment_available": ["DSLR", "Portrait Lens", "Landscape Lens", "Tripod", "Studio Lights"]
        }
    }

    headers = {
        "Content-Type": "application/json",
        "Authorization": "Bearer YOUR_API_TOKEN"
    }

    try:
        response = requests.post(url, json=request_data, headers=headers)
        response.raise_for_status()

        result = response.json()

        print(f"Todo list created successfully!")
        print(f"Stage: {result['stage']}")
        print(f"Conversation ID: {result['conversation_id']}")

        # Process and display the todo list
        if result.get('blocks'):
            for block in result['blocks']:
                if block.get('type') == 'todo_list':
                    print(f"\n📋 {block['list_title']}")
                    for i, item in enumerate(block['items'], 1):
                        print(f"{i}. {item['title']}")
                        if item.get('description'):
                            print(f"   Description: {item['description']}")
                        if item.get('due_datetime'):
                            print(f"   Due: {item['due_datetime']}")

                    if block.get('footer'):
                        print(f"\n💡 {block['footer']}")

        # Display token usage
        if result.get('token_usage'):
            usage = result['token_usage']
            print(f"\n📊 Token Usage:")
            print(f"   Prompt tokens: {usage['prompt_tokens']}")
            print(f"   Completion tokens: {usage['completion_tokens']}")
            print(f"   Total tokens: {usage['total_tokens']}")
            print(f"   Estimated cost: ${usage['cost_estimate_usd']:.4f}")

        return result

    except requests.exceptions.RequestException as e:
        print(f"Error making request: {e}")
        return None
    except json.JSONDecodeError as e:
        print(f"Error parsing response: {e}")
        return None

if __name__ == "__main__":
    create_photography_todo_list()
